function IsJsonString(str) {
    try {
        JSON.parse(str);
    } catch (e) {
        return false;
    }
    return true;
}


function GetFromStorage(name) {
    var res;
    if (!window.localStorage) {
        res = getCookie(name);
    } else {
        res = localStorage.getItem(name);
    }
    return res;
}


function SetToStorage(name, obj) {
    if (!window.localStorage) {
        setCookie(name, JSON.stringify(obj), 999);
    } else {
        localStorage.setItem(name, JSON.stringify(obj));
    }
}


function setCookie(name, value, days) {
    var expires = "";
    if (days) {
        var date = new Date();
        date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
        expires = "; expires=" + date.toUTCString();
    }
    document.cookie = name + "=" + (encodeURIComponent(value) || "") + expires + "; path=/";
}
function getCookie(name) {
    var nameEQ = name + "=";
    var ca = document.cookie.split(';');
    for (var i = 0; i < ca.length; i++) {
        var c = ca[i];
        while (c.charAt(0) == ' ') c = c.substring(1, c.length);
        if (c.indexOf(nameEQ) == 0) return decodeURIComponent(c.substring(nameEQ.length, c.length));
    }
    return null;
}

// Get function (class) name (Specific in IE)
function GetClassName(fun) {
    var className = '';
    if (fun.name) { // e.target.metadata.description.constructor.name
        className = fun.name;
    } else {
        var ret = fun.toString();
        ret = ret.substr('function '.length);
        className = ret.substr(0, ret.indexOf('('));
    }
    return className;
}




