/**
 * Function checks if given longitude and latitude are in current map bounds.
 * Returns true if it is in current map bounds, otherwise returns false 
 */
function isInShownMapArea(cLat, cLong)
{
    var mapBounds = map.getBounds();
    var mapBoundsCenterLatitude = mapBounds["center"]["latitude"];
    var mapBoundsCenterLongitude = mapBounds["center"]["longitude"];
    if (cLat > mapBoundsCenterLatitude - mapBounds["height"] / 2 && cLat < mapBoundsCenterLatitude + mapBounds["height"] / 2  
        && cLong > mapBoundsCenterLongitude - mapBounds["width"] / 2 && cLong < mapBoundsCenterLongitude + mapBounds["width"] / 2)
    {
        return true;
    }
    
    return false;
}


var defaultZoomValue = 14;

/**
 * This function is called whenever any change is made, clicked on checkBox or search on select components.
 * Function takes all elements which are shown on the map at that moment and then determines if change of position and zoom is needed.
 * Function if needed sets new position and zoom on map.
 */
function changePositionAndZoomOnMap() {
    var maxLongitude, minLongitude, maxLatitude, minLatitude;
    // check if we need to change map position and zoom. At start we assume that we don't need change on map.
    var needNewPositionAndZoomOnMap = false;
    
    var sumLatitude = 0.0, sumLongitude = 0.0;
    
    // this is the array of elements that are currently displayed on the map
    var currentElementsOnMap = map.entities;
    var numberOfElements = currentElementsOnMap.getLength();
    
    // Latitude - y 
    // Longitude - x
    // map.entities.get(1).geometry.x
    // map.entities.get(1).geometry.y

    if (numberOfElements !== 0) {
        
        if (!maxLongitude && !minLongitude && !maxLatitude && !minLatitude)
        {
            maxLatitude = map.entities.get(0).geometry.y;
            minLatitude = map.entities.get(0).geometry.y;
            
            maxLongitude = map.entities.get(0).geometry.x;
            minLongitude = map.entities.get(0).geometry.x;
        }
        
        for (var b = 0; b < numberOfElements; b++) {
            var lon = map.entities.get(b).geometry.x;
            var lat = map.entities.get(b).geometry.y;
            
            sumLatitude += lat;
            sumLongitude += lon;
            
            if (needNewPositionAndZoomOnMap === false)
                needNewPositionAndZoomOnMap = !isInShownMapArea(lat, lon);
            
            if (lat > maxLatitude)
            {
                maxLatitude = lat;
            }
            else
            {
                if (lat < minLatitude)
                {
                    minLatitude = lat;
                }
            }
            
            if (lon > maxLongitude)
            {
                maxLongitude = lon;
            }
            else
            {
                if (lon < minLongitude)
                {
                    minLongitude = lon;
                }
            }
        }
        
        console.log("Max latitude: " + maxLatitude + "\nMin latitude: " + minLatitude + "\nMax longitude: " + maxLongitude + "\nMin longitude: " + minLongitude);
        
        if (needNewPositionAndZoomOnMap === true && numberOfElements !== 0)
        {
            var boundsRect = new Microsoft.Maps.LocationRect(new Microsoft.Maps.Location(sumLatitude / numberOfElements, sumLongitude / numberOfElements), maxLongitude - minLongitude, maxLatitude - minLatitude);
            map.setView({bounds:boundsRect, padding: 100});
            
            if (map.getZoom() > defaultZoomValue)
            {
                map.setView({center: new Microsoft.Maps.Location(sumLatitude / numberOfElements, sumLongitude / numberOfElements), zoom: defaultZoomValue});
            }
            
            console.log(map.getCenter());
        }
    }
}

// Fixing InfoBox (remove Title from begining of string, replace comma and spaces if exists on beginning
function ClearInfoBox(title, info) {
    var rez = info.replace(title, '').trim();
    if (rez.startsWith(',')) {
        rez = rez.substring(1);
    }
    return rez.trim();
}

function ReplaceDoubleQuotes(s) {
    var rez = s.replace(/\"/g, "&quot;");
    return rez;
}

