function oClinics(ClinicType, clinicData) {
    this.Type = ClinicType;
    this.Name = clinicData.name;
    this.MetroStation = clinicData.metroSt;
    this.Address = clinicData.addr;
    this.City = clinicData.city;
    this.AddDesc = clinicData.addDesc;
    this.Longitude = clinicData.longitude;
    this.Latitude = clinicData.latitude;
    this.WorkHours = clinicData.workHours;
    this.Telephone = clinicData.telephone;
    this.Email = clinicData.email;
    this.WebSite = clinicData.webSite;
    this.InfoBox = clinicData.info;
    this.Distance = 0;
}


